import React, { useEffect, useState} from 'react'
import './Login.css'
import MovieBox from './MovieBox'
import axios from 'axios'
import Pager from './Pager'
import MovieHomeNav from '../Components/MoviHomeNav'

const Movies = () => {
const [list, setList]= useState()
let [page, setPage]= useState(1)

useEffect(() =>{
   axios
   .get(`https://api.themoviedb.org/3/trending/movie/day?api_key=977eefe575b9168b5e7fd6e6fabc58f1&language=en-US&page=${page}`)  
   .then(({data}) => setList(data.results))
   .catch((err) => console.log(err))
},[page])

  return (
    <div> <MovieHomeNav />
      <div className="container">
            <h1>Trending... Movie & TvSeries</h1>
              <Pager  page={page} setpage={setPage} />
        <div className="grid">  
    
          {
            list && list.map((list) => <MovieBox list ={list} />)
          }
        </div>
        <Pager  page={page} setpage={setPage} />
          </div>
          </div>
  
    
    
  )
}

export default Movies
